function initHomeArticlesToggle(){
  try{
    const details = document.querySelector('details.home-articles-toggle');
    if (!details) return;
    const mq = window.matchMedia('(min-width: 900px)');
    const apply = () => {
      details.open = mq.matches; // desktop: open, mobile: closed
    };
    apply();
    if (mq.addEventListener) mq.addEventListener('change', apply);
    else mq.addListener(apply);
  }catch(_e){}
}
document.addEventListener('DOMContentLoaded', initHomeArticlesToggle);

function bindSliderNav(){
  try{
    document.querySelectorAll('.slider-container').forEach(container => {
      const track = container.querySelector('.slider-track');
      if (!track) return;
      const prev = container.querySelector('.slider-nav.prev');
      const next = container.querySelector('.slider-nav.next');

      const scrollByAmount = () => {
        // Prefer card width if present; fallback to container width
        const firstCard = track.querySelector('.slide') || track.querySelector('.card');
        const cardW = firstCard ? (firstCard.getBoundingClientRect().width || 0) : 0;
        const gap = parseFloat(getComputedStyle(track).columnGap || getComputedStyle(track).gap || '0') || 0;
        return Math.max(240, Math.min(track.getBoundingClientRect().width, cardW + gap || track.getBoundingClientRect().width * 0.9));
      };

      if (prev){
        prev.addEventListener('click', (e)=>{ e.preventDefault(); track.scrollBy({ left: -scrollByAmount(), behavior: 'smooth' }); });
      }
      if (next){
        next.addEventListener('click', (e)=>{ e.preventDefault(); track.scrollBy({ left:  scrollByAmount(), behavior: 'smooth' }); });
      }
    });
  }catch(_e){}
}
document.addEventListener('DOMContentLoaded', bindSliderNav);



