// Convert Persian/Arabic-Indic digits to Latin digits (for EN UI)
function digitsToLatin(str) {
  if (typeof str !== 'string') return str;
  const map = {
    'ฐ':'0','ฑ':'1','ฒ':'2','ณ':'3','ด':'4','ต':'5','ถ':'6','ท':'7','ธ':'8','น':'9',
    'ู':'0','ูก':'1','ูข':'2','ูฃ':'3','ูค':'4','ูฅ':'5','ูฆ':'6','ูง':'7','ูจ':'8','ูฉ':'9'
  };
  return str.replace(/[ฐ-นู-ูฉ]/g, ch => map[ch] ?? ch);
}

// Walk all text nodes and convert digits (safe because we only touch digits)
function convertAllDigitsToLatin(rootEl) {
  try {
    const root = rootEl || document.body;
    if (!root) return;
    const walker = document.createTreeWalker(root, NodeFilter.SHOW_TEXT, {
      acceptNode(node) {
        if (!node || !node.nodeValue) return NodeFilter.FILTER_REJECT;
        // skip script/style-ish contexts
        const p = node.parentElement;
        if (!p) return NodeFilter.FILTER_ACCEPT;
        const tag = (p.tagName || '').toLowerCase();
        if (tag === 'script' || tag === 'style' || tag === 'noscript') return NodeFilter.FILTER_REJECT;
        return NodeFilter.FILTER_ACCEPT;
      }
    });
    let n;
    while ((n = walker.nextNode())) {
      const v = n.nodeValue;
      const nv = digitsToLatin(v);
      if (nv !== v) n.nodeValue = nv;
    }
  } catch(e) {}
}
