  // ---- Price estimator (tools/pricing) ----
  function clamp(n, min, max){ return Math.max(min, Math.min(max, n)); }

  function formatToman(n){
    try{
      return new Intl.NumberFormat('fa-IR').format(Math.round(n)) + ' ุชููุงู';
    }catch(e){
      return String(Math.round(n)) + ' ุชููุงู';
    }
  }

  function estimateSurfaceMM2(shape, dims){
    // returns mm^2 approximate external surface area
    if(shape === 'cyl'){
      var D = Math.max(1, Number(dims.D||0));
      var H = Math.max(1, Number(dims.H||0));
      var r = D/2;
      return (Math.PI*D*H) + (2*Math.PI*r*r);
    }
    // box
    var L = Math.max(1, Number(dims.L||0));
    var W = Math.max(1, Number(dims.W||0));
    var H2 = Math.max(1, Number(dims.H||0));
    return 2*(L*W + L*H2 + W*H2);
  }

  function initPriceEstimator(){
    // Supports both legacy ids (priceForm/shape/...) and new ids (pc_*)
    var shape = document.getElementById('pc_shape') || document.getElementById('shape');
    var L_in  = document.getElementById('pc_len')  || document.getElementById('L');
    var W_in  = document.getElementById('pc_w')    || document.getElementById('W');
    var H_in  = document.getElementById('pc_h')    || document.getElementById('H');
    var D_in  = document.getElementById('pc_d')    || document.getElementById('D');
    var matEl = document.getElementById('pc_mat')  || document.getElementById('material');
    var finEl = document.getElementById('pc_finish') || document.getElementById('finish');
    var colEl = document.getElementById('pc_color')  || document.getElementById('color');
    var qtyEl = document.getElementById('pc_qty') || document.getElementById('qty');
    var cxEl  = document.getElementById('pc_cx')  || document.getElementById('HC');
    var packEl= document.getElementById('pc_pack');
    var jigEl = document.getElementById('pc_jig');
    var setupEl = document.getElementById('pc_setup');
    var outCap = document.getElementById('pc_cap');
    var outRuns= document.getElementById('pc_runs');

    var rateEl= document.getElementById('pc_rate'); // only new UI
    var btn   = document.getElementById('pc_calc');
    var outArea = document.getElementById('pc_area');
    var outScore= document.getElementById('pc_score');
    var outPrice= document.getElementById('pc_price');
    var outBreak= document.getElementById('pc_breakdown');

    if(!shape || !L_in || !W_in || !H_in || !D_in || !matEl || !finEl || !colEl || !qtyEl) return;

    function num(v){ v = Number(v||0); return isFinite(v) ? v : 0; }
    function clampMin(v, min){ v = num(v); return (v>0 ? Math.max(v,min) : 0); }

    // mm^2 -> m^2
    function area_m2(shapeVal, L, W, H, D){
      // Inputs in mm
      shapeVal = String(shapeVal || 'plate');
      // Avoid negative
      L = Math.max(0, L); W = Math.max(0, W); H = Math.max(0, H); D = Math.max(0, D);

      if(shapeVal === 'box'){
        // Surface of rectangular solid: 2(LW+LH+WH)
        if(L===0 || W===0 || H===0) return 0;
        return (2*(L*W + L*H + W*H)) / 1e6;
      }
      if(shapeVal === 'cyl'){
        // Cylinder: lateral + two ends
        if(L===0 || D===0) return 0;
        var r = D/2;
        return (Math.PI*D*L + 2*Math.PI*r*r) / 1e6;
      }
      // plate: approximate as two faces + edges using H as thickness (if provided)
      if(L===0 || W===0) return 0;
      var a = 2*(L*W);
      if(H>0){
        a += 2*H*(L+W);
      }
      return a / 1e6;
    }

    // Coefficients (tunable)
    var materialK = { steel: 1.05, iron: 1.00, al: 1.08, abs: 1.45, pp: 1.60 };
    var finishK   = { gloss: 1.00, matte: 1.06 };
    var colorK    = { silver: 1.00, gold: 1.18, rosegold: 1.22, smoke: 1.12, rainbow: 1.30 };
    var complexK  = { simple: 1.00, medium: 1.10, complex: 1.22 };

    function qtyK(q){
      q = Math.max(1, Math.floor(num(q)));
      if(q >= 1000) return 0.66;
      if(q >= 500)  return 0.72;
      if(q >= 200)  return 0.80;
      if(q >= 100)  return 0.88;
      if(q >= 50)   return 0.95;
      if(q >= 10)   return 1.00;
      return 1.18; // single/very small batch overhead
    }

    function fmt(n, digits){
      try{
        return new Intl.NumberFormat('fa-IR', { maximumFractionDigits: digits||2 }).format(n);
      }catch(e){
        return String(Math.round(n*100)/100);
      }
    }
    function fmtEn(n, digits){
      try{
        return new Intl.NumberFormat('en-US', { maximumFractionDigits: digits||2 }).format(n);
      }catch(e){
        return String(Math.round(n*100)/100);
      }
    }

    function currentLang(){
      try{
        return (document.documentElement.getAttribute('lang') || localStorage.getItem('kavico:lang') || localStorage.getItem('lang') || 'fa').startsWith('en') ? 'en' : 'fa';
      }catch(e){ return 'fa'; }
    }

    function render(){
      var L = clampMin(L_in.value, 0);
      var W = clampMin(W_in.value, 0);
      var H = clampMin(H_in.value, 0);
      var D = clampMin(D_in.value, 0);

      var a = area_m2(shape.value, L, W, H, D);
      var mat = (matEl.value || 'iron');
      var fin = (finEl.value || 'gloss');
      var col = (colEl.value || 'silver');
      var cx  = (cxEl && cxEl.value) ? cxEl.value : 'simple';
      var q   = qtyK(qtyEl.value);

      var mk = materialK[mat] || 1.0;
      var fk = finishK[fin] || 1.0;
      var ck = colorK[col] || 1.0;
      var xk = complexK[cx] || 1.0;

      // Score: baseIndexPerM2 * TOTAL area * multipliers * qty factor * runs factor
      var baseIndexPerM2 = 100; // index scale
      var qtyN = Math.max(1, Math.floor(num(qtyEl.value||1)));

      // Device chamber: 100x100x170 cm => in mm
      var CHX = 1000, CHY = 1000, CHZ = 1700;
      var spacing = 10; // mm clearance between parts

      function partDimsMM(){
        // Use bounding-box dims in mm for rough packing
        var dx = L, dy = W, dz = H;
        if(sh === 'cyl'){
          var d = Math.max(D, 0);
          dx = d; dy = d; dz = Math.max(H, 0);
        }
        // plate: thickness = H
        return [Math.max(dx,1), Math.max(dy,1), Math.max(dz,1)];
      }

      function estimatePiecesPerRun(){
        var user = packEl ? Math.floor(num(packEl.value||0)) : 0;
        var hasJig = jigEl ? !!jigEl.checked : false;
        var mult = hasJig ? 1.5 : 1.0;

        if(user && user > 0){
          return Math.max(1, Math.floor(user * mult));
        }

        var dims = partDimsMM();
        var dx = dims[0] + spacing;
        var dy = dims[1] + spacing;
        var dz = dims[2] + spacing;

        var nx = Math.floor(CHX / dx);
        var ny = Math.floor(CHY / dy);
        var nz = Math.floor(CHZ / dz);

        var est = Math.max(1, nx * ny * nz);
        est = Math.floor(est * mult);
        return Math.max(1, est);
      }

      var pcsPerRun = estimatePiecesPerRun();
      var runs = Math.max(1, Math.ceil(qtyN / pcsPerRun));

      // Show capacity + runs
      if(outCap){
        var langCap = currentLang();
        outCap.textContent = (langCap==='en') ? String(pcsPerRun) : String(pcsPerRun);
      }
      if(outRuns){
        var langRun = currentLang();
        outRuns.textContent = (langRun==='en') ? String(runs) : String(runs);
      }

      // Runs factor: more runs => more setup time => higher cost
      var runFactor = 1 + Math.min(0.60, Math.max(0, runs - 1) * 0.06);

      // Total surface area for the order
      var totalArea = a * qtyN;

      var score = (totalArea * baseIndexPerM2) * mk * fk * ck * xk * q * runFactor;

      // Minimum charge (index)
      var minScore = 80;
      if(score > 0 && score < minScore) score = minScore;

      // area output
      if(outArea){
        var lang = currentLang();
        var aText = (lang==='en') ? (fmtEn(a,3) + " mยฒ") : (fmt(a,3) + " mยฒ");
        outArea.textContent = a>0 ? aText : "โ";
      }
      if(outScore){
        var lang2 = currentLang();
        outScore.textContent = score>0 ? ((lang2==='en') ? fmtEn(score,2) : fmt(score,2)) : "โ";
      }

      // Price estimate if base rate provided (in currency per mยฒ). We treat it as "per mยฒ" baseline.
      if(outPrice){
        var rate = rateEl ? num(rateEl.value) : 0;
        if(rate>0 && a>0){
          // Convert index back to multiplier around 1 for base materials:
          var norm = score / (a * baseIndexPerM2);
          // Estimated price: area * rate * norm
          var est = a * rate * norm;
          // Add small setup for very low qty
          var setup = (Number(qtyEl.value||1) < 10) ? Math.max(0.08*est, 0) : 0;
          var total = est + setup;

          // Show range ยฑ10% (initial estimate)
          var lo = total * 0.90;
          var hi = total * 1.10;

          var lang3 = currentLang();
          var unit = (lang3==='en') ? " Toman" : " ุชููุงู";
          outPrice.textContent = (lang3==='en')
            ? ("Estimated: " + fmtEn(lo,0) + " โ " + fmtEn(hi,0) + unit)
            : ("ุญุฏูุฏ: " + fmt(lo,0) + " ุชุง " + fmt(hi,0) + unit);
        }else{
          outPrice.textContent = "";
        }
      }

      // Breakdown
      if(outBreak){
        var lang4 = currentLang();
        function row(labelFa, labelEn, val){
          var lab = (lang4==='en') ? labelEn : labelFa;
          var v = (lang4==='en') ? fmtEn(val,2) : fmt(val,2);
          return '<div class="bd-row"><div class="bd-l">'+escapeHtml(lab)+'</div><div class="bd-v">'+escapeHtml(v)+'</div></div>';
        }
        var html = '';
        html += row('ุถุฑุจ ุฌูุณ','Material coeff.', mk);
        html += row('ุถุฑุจ ููุด','Finish coeff.', fk);
        html += row('ุถุฑุจ ุฑูฺฏ','Color coeff.', ck);
        html += row('ูพฺุฏฺฏ','Complexity', xk);
        html += row('ุชุฑุงฺ','Quantity factor', q);
        outBreak.innerHTML = html;
      }
    }

    function escapeHtml(s){
      return String(s).replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');
    }

    // Bind
    var inputs=[shape,L_in,W_in,H_in,D_in,matEl,finEl,colEl,qtyEl];
    if(cxEl) inputs.push(cxEl);
    if(rateEl) inputs.push(rateEl);

    inputs.forEach(function(el){
      if(!el) return;
      el.addEventListener('input', render);
      el.addEventListener('change', render);
    });
    if(btn){
      btn.addEventListener('click', function(e){
        e.preventDefault();
        render();
      });
    }

    // initial render
    render();
  }


