function formatISODate(iso, lang){
  try{
    if (!iso) return '';
    const d = new Date(iso);
    if (isNaN(d.getTime())) return iso;
    const locale = (lang === 'fa') ? 'fa-IR' : 'en-US';
    const fmt = new Intl.DateTimeFormat(locale, { year:'numeric', month:'short', day:'2-digit' });
    return fmt.format(d);
  }catch(_e){
    return iso || '';
  }
}



